const generateId = () => Math.random().toString(36).substr(2, 9);

const formatDate = (date) => {
  const d = new Date(date);
  const now = new Date();
  const diff = now - d;
  if (diff < 60000) return 'Just now';
  if (diff < 3600000) return `${Math.floor(diff / 60000)}m ago`;
  if (diff < 86400000) return `${Math.floor(diff / 3600000)}h ago`;
  if (diff < 604800000) return `${Math.floor(diff / 86400000)}d ago`;
  return d.toLocaleDateString();
};

const renderLatexPreview = (latex) => {
  if (!latex) return '';
  return latex
    .replace(/\\frac\{([^}]*)\}\{([^}]*)\}/g, '<sup>$1</sup>⁄<sub>$2</sub>')
    .replace(/\\sqrt\{([^}]*)\}/g, '√($1)')
    .replace(/\\int/g, '∫').replace(/\\sum/g, '∑').replace(/\\prod/g, '∏')
    .replace(/\\infty/g, '∞').replace(/\\pi/g, 'π').replace(/\\alpha/g, 'α')
    .replace(/\\beta/g, 'β').replace(/\\gamma/g, 'γ').replace(/\\delta/g, 'δ')
    .replace(/\\theta/g, 'θ').replace(/\\lambda/g, 'λ').replace(/\\mu/g, 'μ')
    .replace(/\\sigma/g, 'σ').replace(/\\omega/g, 'ω').replace(/\\phi/g, 'φ')
    .replace(/\\partial/g, '∂').replace(/\\nabla/g, '∇').replace(/\\rightarrow/g, '→')
    .replace(/\\leftarrow/g, '←').replace(/\\Rightarrow/g, '⇒')
    .replace(/\\leq/g, '≤').replace(/\\geq/g, '≥').replace(/\\neq/g, '≠')
    .replace(/\\approx/g, '≈').replace(/\\times/g, '×').replace(/\\cdot/g, '·').replace(/\\pm/g, '±')
    .replace(/\^(\w)/g, '<sup>$1</sup>').replace(/\^\{([^}]*)\}/g, '<sup>$1</sup>')
    .replace(/_(\w)/g, '<sub>$1</sub>').replace(/_\{([^}]*)\}/g, '<sub>$1</sub>');
};

const parseLatexPreamble = (content) => {
  if (!content) return { documentClass: 'article', structures: LATEX_STRUCTURES.article, customTypes: {} };
  
  const classMatch = content.match(/\\documentclass(?:\[.*?\])?\{(\w+)\}/);
  const documentClass = classMatch ? classMatch[1].toLowerCase() : 'article';
  let structures = [...(LATEX_STRUCTURES[documentClass] || LATEX_STRUCTURES.article)];
  const customTypes = {};
  
  // Handle French babel
  if (content.includes('french')) {
    structures = structures.map(s => s === 'chapter' ? 'chapitre' : s === 'part' ? 'partie' : s);
  }
  
  // Detect \newcommand{\commandname} patterns
  const newCommandRegex = /\\(?:new|renew)command\{\\(\w+)\}/g;
  let match;
  while ((match = newCommandRegex.exec(content)) !== null) {
    const cmdName = match[1].toLowerCase();
    // Only add if it looks like a section command and isn't already in NODE_TYPES
    if (!NODE_TYPES[cmdName] && !structures.includes(cmdName)) {
      structures.push(cmdName);
      customTypes[cmdName] = {
        icon: '◆',
        color: '#9333ea',
        name: cmdName.charAt(0).toUpperCase() + cmdName.slice(1),
        latex: `\\${cmdName}`
      };
    }
  }
  
  // Detect % STRUCTURES: acte, scene, prologue style comments
  const structureComment = content.match(/%\s*(?:STRUCTURES?|SECTIONS?)\s*:\s*(.+)/i);
  if (structureComment) {
    const customStructures = structureComment[1].split(',').map(s => s.trim().toLowerCase()).filter(s => s);
    customStructures.forEach(s => {
      if (!structures.includes(s)) {
        structures.push(s);
        if (!NODE_TYPES[s]) {
          customTypes[s] = {
            icon: '◇',
            color: '#a855f7',
            name: s.charAt(0).toUpperCase() + s.slice(1),
            latex: `\\${s}`
          };
        }
      }
    });
  }
  
  // Detect explicit \section, \chapter, \acte, etc. usage in preamble
  const usedCommands = content.match(/\\(acte|scene|prologue|epilogue|chapitre|partie|article|theorem|lemma|definition|proposition|corollary|remark|example|proof|exercise|solution)(?:\s|\{|\[)/gi);
  if (usedCommands) {
    usedCommands.forEach(cmd => {
      const cmdName = cmd.replace(/[\\\s\{\[]/g, '').toLowerCase();
      if (!structures.includes(cmdName)) {
        structures.push(cmdName);
        if (!NODE_TYPES[cmdName]) {
          customTypes[cmdName] = {
            icon: '◈',
            color: '#c084fc',
            name: cmdName.charAt(0).toUpperCase() + cmdName.slice(1),
            latex: `\\${cmdName}`
          };
        }
      }
    });
  }
  
  // Detect amsthm-style environments: \newtheorem{theorem}{Theorem}
  const theoremRegex = /\\newtheorem\{(\w+)\}/g;
  while ((match = theoremRegex.exec(content)) !== null) {
    const envName = match[1].toLowerCase();
    if (!structures.includes(envName)) {
      structures.push(envName);
      customTypes[envName] = {
        icon: '∎',
        color: '#06b6d4',
        name: envName.charAt(0).toUpperCase() + envName.slice(1),
        latex: `\\begin{${envName}}`
      };
    }
  }
  
  return { documentClass, structures, customTypes };
};

const getOrderedNodes = (nodes, connections) => {
  // Get all non-parameter nodes
  const contentNodes = nodes.filter(n => n.type !== 'parameters');
  
  // Create a map of node id to its connection order
  const orderMap = {};
  connections.forEach(c => {
    if (c.order !== undefined) {
      orderMap[c.to] = c.order;
    }
  });
  
  // Separate nodes with connections (have order) from those without
  const withOrder = contentNodes.filter(n => orderMap[n.id] !== undefined);
  const withoutOrder = contentNodes.filter(n => orderMap[n.id] === undefined);
  
  // Sort nodes with order by their connection order
  withOrder.sort((a, b) => orderMap[a.id] - orderMap[b.id]);
  
  // Sort nodes without order by Y position
  withoutOrder.sort((a, b) => a.y - b.y);
  
  // Combine: ordered nodes first, then unconnected nodes
  return [...withOrder, ...withoutOrder];
};

