const NewProjectModal = ({ onClose, onCreate }) => {
  const [name, setName] = useState('');
  const [documentClass, setDocumentClass] = useState('article');
  const [author, setAuthor] = useState('');
  const [packages, setPackages] = useState(['amsmath', 'amssymb', 'graphicx']);
  
  const availablePackages = [
    { id: 'amsmath', name: 'amsmath', desc: 'Math equations' },
    { id: 'amssymb', name: 'amssymb', desc: 'Math symbols' },
    { id: 'graphicx', name: 'graphicx', desc: 'Images' },
    { id: 'hyperref', name: 'hyperref', desc: 'Links' },
    { id: 'listings', name: 'listings', desc: 'Code blocks' },
    { id: 'tikz', name: 'tikz', desc: 'Diagrams' },
    { id: 'biblatex', name: 'biblatex', desc: 'Bibliography' },
    { id: 'geometry', name: 'geometry', desc: 'Page layout' },
  ];
  
  const togglePackage = (pkg) => {
    setPackages(prev => prev.includes(pkg) ? prev.filter(p => p !== pkg) : [...prev, pkg]);
  };
  
  const handleCreate = () => {
    if (!name.trim()) return;
    
    const preamble = `\\documentclass{${documentClass}}
${packages.map(p => `\\usepackage{${p}}`).join('\n')}
\\usepackage[utf8]{inputenc}
\\usepackage[T1]{fontenc}

\\title{${name}}
\\author{${author || 'Author Name'}}
\\date{\\today}`;
    
    onCreate({
      name: name.trim(),
      documentClass,
      preamble
    });
  };
  
  return (
    <div className="fixed inset-0 z-[9999] bg-black/95 flex items-center justify-center p-4 overflow-y-auto" onClick={onClose}>
      <div 
        className="bg-zinc-900 border border-zinc-800 rounded-3xl w-full max-w-lg shadow-2xl my-auto"
        onClick={(e) => e.stopPropagation()}
      >
        {/* Header */}
        <div className="relative h-20 bg-gradient-to-b from-orange-500/10 to-transparent flex items-center justify-center rounded-t-3xl">
          <h2 className="text-2xl font-bold text-white">New Project</h2>
        </div>
        
        <div className="p-6 space-y-5 max-h-[60vh] overflow-y-auto">
          {/* Project Name */}
          <div>
            <label className="block text-xs text-zinc-500 uppercase tracking-wider mb-2">Project Name</label>
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="My Research Paper"
              autoFocus
              className="w-full bg-zinc-800/50 border border-zinc-700 rounded-xl px-4 py-3 text-white placeholder-zinc-600 focus:outline-none focus:border-orange-500/50 transition-colors"
            />
          </div>
          
          {/* Document Class */}
          <div>
            <label className="block text-xs text-zinc-500 uppercase tracking-wider mb-2">Document Type</label>
            <div className="grid grid-cols-3 gap-2">
              {DOCUMENT_CLASSES.map(dc => (
                <button
                  key={dc.value}
                  onClick={() => setDocumentClass(dc.value)}
                  className={`p-3 rounded-xl border transition-all text-left ${
                    documentClass === dc.value 
                      ? 'border-orange-500 bg-orange-500/10' 
                      : 'border-zinc-700 hover:border-zinc-600 bg-zinc-800/30'
                  }`}
                >
                  <div className={`text-sm font-medium ${documentClass === dc.value ? 'text-orange-400' : 'text-zinc-300'}`}>
                    {dc.label}
                  </div>
                  <div className="text-[10px] text-zinc-500 mt-0.5">{dc.desc}</div>
                </button>
              ))}
            </div>
          </div>
          
          {/* Author */}
          <div>
            <label className="block text-xs text-zinc-500 uppercase tracking-wider mb-2">Author (optional)</label>
            <input
              type="text"
              value={author}
              onChange={(e) => setAuthor(e.target.value)}
              placeholder="Your name"
              className="w-full bg-zinc-800/50 border border-zinc-700 rounded-xl px-4 py-3 text-white placeholder-zinc-600 focus:outline-none focus:border-orange-500/50 transition-colors"
            />
          </div>
          
          {/* Packages */}
          <div>
            <label className="block text-xs text-zinc-500 uppercase tracking-wider mb-2">Packages</label>
            <div className="flex flex-wrap gap-2">
              {availablePackages.map(pkg => (
                <button
                  key={pkg.id}
                  onClick={() => togglePackage(pkg.id)}
                  className={`px-3 py-1.5 rounded-lg text-xs transition-all ${
                    packages.includes(pkg.id)
                      ? 'bg-orange-500/20 text-orange-400 border border-orange-500/30'
                      : 'bg-zinc-800/50 text-zinc-400 border border-zinc-700 hover:border-zinc-600'
                  }`}
                  title={pkg.desc}
                >
                  {pkg.name}
                </button>
              ))}
            </div>
          </div>
        </div>
        
        {/* Footer */}
        <div className="px-6 py-4 border-t border-zinc-800 flex justify-end gap-3">
          <button
            onClick={onClose}
            className="px-5 py-2.5 rounded-xl text-zinc-400 hover:text-white transition-colors"
          >
            Cancel
          </button>
          <button
            onClick={handleCreate}
            disabled={!name.trim()}
            className="px-6 py-2.5 rounded-xl bg-gradient-to-r from-orange-500 to-amber-500 text-white font-medium disabled:opacity-50 disabled:cursor-not-allowed hover:from-orange-400 hover:to-amber-400 transition-all"
          >
            Create Project
          </button>
        </div>
      </div>
    </div>
  );
};

