// TIMELINE SLIDER (Time-Travel)
// ═══════════════════════════════════════════════════════════════════════════════════════

const TimelineSlider = ({ timeline, currentIndex, isTimeTraveling, onTravelTo, onReturnToPresent, onApply, onClose }) => {
  const [hoveredIndex, setHoveredIndex] = useState(null);
  const sliderRef = useRef(null);
  
  const formatTime = (timestamp) => {
    const date = new Date(timestamp);
    const now = new Date();
    const diffMs = now - date;
    const diffMins = Math.floor(diffMs / 60000);
    const diffHours = Math.floor(diffMs / 3600000);
    
    if (diffMins < 1) return 'Just now';
    if (diffMins < 60) return `${diffMins}m ago`;
    if (diffHours < 24) return `${diffHours}h ago`;
    return date.toLocaleDateString();
  };
  
  const handleSliderClick = (e) => {
    if (!sliderRef.current) return;
    const rect = sliderRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const percent = x / rect.width;
    const index = Math.round(percent * (timeline.length - 1));
    onTravelTo(Math.max(0, Math.min(timeline.length - 1, index)));
  };
  
  const handleSliderHover = (e) => {
    if (!sliderRef.current) return;
    const rect = sliderRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const percent = x / rect.width;
    const index = Math.round(percent * (timeline.length - 1));
    setHoveredIndex(Math.max(0, Math.min(timeline.length - 1, index)));
  };
  
  return (
    <motion.div 
      className="absolute bottom-4 left-1/2 -translate-x-1/2 z-50"
      initial={{ opacity: 0, y: 50 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: 50 }}
      transition={{ type: 'spring', damping: 25 }}
    >
      <div className="bg-zinc-900/95 backdrop-blur-xl border border-zinc-700 rounded-2xl p-4 shadow-2xl min-w-[400px] max-w-[600px]">
        {/* Header */}
        <div className="flex items-center justify-between mb-3">
          <div className="flex items-center gap-2">
            <div className="w-8 h-8 rounded-lg bg-gradient-to-br from-violet-500 to-purple-600 flex items-center justify-center">
              <svg className="w-4 h-4 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
            </div>
            <div>
              <h3 className="text-sm font-semibold text-white">Time Travel</h3>
              <p className="text-[10px] text-zinc-500">{timeline.length} snapshots • {isTimeTraveling ? 'Viewing past' : 'Present'}</p>
            </div>
          </div>
          <button onClick={onClose} className="w-8 h-8 rounded-lg bg-zinc-800 hover:bg-zinc-700 text-zinc-400 hover:text-white transition-colors flex items-center justify-center">
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>
        
        {/* Timeline visualization */}
        <div className="relative mb-3">
          {/* Track background */}
          <div 
            ref={sliderRef}
            className="h-12 bg-zinc-800/50 rounded-xl cursor-pointer relative overflow-hidden"
            onClick={handleSliderClick}
            onMouseMove={handleSliderHover}
            onMouseLeave={() => setHoveredIndex(null)}
          >
            {/* Gradient fill up to current position */}
            <div 
              className="absolute inset-y-0 left-0 rounded-xl"
              style={{
                width: `${(currentIndex / (timeline.length - 1)) * 100}%`,
                background: 'linear-gradient(90deg, rgba(139, 92, 246, 0.3), rgba(168, 85, 247, 0.5))'
              }}
            />
            
            {/* Snapshot markers */}
            <div className="absolute inset-0 flex items-center px-2">
              {timeline.map((snapshot, i) => {
                const position = timeline.length === 1 ? 50 : (i / (timeline.length - 1)) * 100;
                const isCurrent = i === currentIndex;
                const isHovered = i === hoveredIndex;
                
                return (
                  <div
                    key={i}
                    className="absolute transition-all duration-150"
                    style={{ left: `${position}%`, transform: 'translateX(-50%)' }}
                  >
                    {/* Marker dot */}
                    <div 
                      className={`rounded-full transition-all duration-150 ${
                        isCurrent 
                          ? 'w-4 h-4 bg-violet-500 shadow-lg shadow-violet-500/50' 
                          : isHovered 
                            ? 'w-3 h-3 bg-violet-400' 
                            : 'w-2 h-2 bg-zinc-500'
                      }`}
                    />
                    
                    {/* Hover tooltip */}
                    {isHovered && !isCurrent && (
                      <div className="absolute bottom-full mb-2 left-1/2 -translate-x-1/2 whitespace-nowrap">
                        <div className="bg-zinc-800 text-white text-[10px] px-2 py-1 rounded-lg shadow-lg">
                          {snapshot.label} • {formatTime(snapshot.timestamp)}
                        </div>
                      </div>
                    )}
                  </div>
                );
              })}
            </div>
            
            {/* Current position indicator */}
            <div 
              className="absolute top-0 bottom-0 w-0.5 bg-violet-400"
              style={{ left: `${(currentIndex / (timeline.length - 1)) * 100}%` }}
            />
          </div>
          
          {/* Time labels */}
          <div className="flex justify-between mt-1 px-1">
            <span className="text-[9px] text-zinc-600">{formatTime(timeline[0]?.timestamp)}</span>
            <span className="text-[9px] text-zinc-600">{formatTime(timeline[timeline.length - 1]?.timestamp)}</span>
          </div>
        </div>
        
        {/* Controls */}
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-2">
            {/* Step backward */}
            <button 
              onClick={() => onTravelTo(Math.max(0, currentIndex - 1))}
              disabled={currentIndex === 0}
              className="w-8 h-8 rounded-lg bg-zinc-800 hover:bg-zinc-700 disabled:opacity-30 disabled:cursor-not-allowed text-zinc-400 hover:text-white transition-colors flex items-center justify-center"
            >
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
              </svg>
            </button>
            
            {/* Step forward */}
            <button 
              onClick={() => onTravelTo(Math.min(timeline.length - 1, currentIndex + 1))}
              disabled={currentIndex === timeline.length - 1}
              className="w-8 h-8 rounded-lg bg-zinc-800 hover:bg-zinc-700 disabled:opacity-30 disabled:cursor-not-allowed text-zinc-400 hover:text-white transition-colors flex items-center justify-center"
            >
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
              </svg>
            </button>
            
            <span className="text-xs text-zinc-500 ml-2">
              {currentIndex + 1} / {timeline.length}
            </span>
          </div>
          
          <div className="flex items-center gap-2">
            {isTimeTraveling && (
              <>
                <button 
                  onClick={onApply}
                  className="px-3 py-1.5 rounded-lg bg-emerald-500/20 text-emerald-400 hover:bg-emerald-500/30 transition-colors text-xs font-medium flex items-center gap-1"
                >
                  <svg className="w-3 h-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                  </svg>
                  Restore this state
                </button>
                <button 
                  onClick={onReturnToPresent}
                  className="px-3 py-1.5 rounded-lg bg-zinc-700/50 text-zinc-300 hover:bg-zinc-600/50 transition-colors text-xs font-medium flex items-center gap-1"
                >
                  <svg className="w-3 h-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                  </svg>
                  Cancel
                </button>
              </>
            )}
          </div>
        </div>
      </div>
    </motion.div>
  );
};

// ═══════════════════════════════════════════════════════════════════════════════════════
// MINIMAP
// ═══════════════════════════════════════════════════════════════════════════════════════

const Minimap = ({ nodes, connections, nebulas = [], pan, zoom, canvasSize, onNavigate }) => {
  const scale = 0.05;
  const width = 160;
  const height = 120;
  
  // Calculate bounds of all nodes and nebulas
  const bounds = useMemo(() => {
    const allItems = [...nodes, ...nebulas];
    if (allItems.length === 0) return { minX: -500, maxX: 500, minY: -500, maxY: 500 };
    const xs = allItems.map(n => n.x);
    const ys = allItems.map(n => n.y);
    const padding = 200;
    return {
      minX: Math.min(...xs) - padding,
      maxX: Math.max(...xs) + padding,
      minY: Math.min(...ys) - padding,
      maxY: Math.max(...ys) + padding
    };
  }, [nodes, nebulas]);
  
  const rangeX = bounds.maxX - bounds.minX;
  const rangeY = bounds.maxY - bounds.minY;
  const scaleX = width / rangeX;
  const scaleY = height / rangeY;
  const s = Math.min(scaleX, scaleY);
  
  const toMiniX = (x) => (x - bounds.minX) * s;
  const toMiniY = (y) => (y - bounds.minY) * s;
  
  // Viewport rectangle
  const vpWidth = (canvasSize.width / zoom) * s;
  const vpHeight = (canvasSize.height / zoom) * s;
  const vpX = toMiniX(-pan.x / zoom);
  const vpY = toMiniY(-pan.y / zoom);
  
  return (
    <div className="absolute bottom-4 left-4 z-40 bg-zinc-900/90 border border-zinc-700 rounded-xl overflow-hidden backdrop-blur-sm"
      style={{ width, height }}>
      <svg width={width} height={height}>
        {/* Nebulas */}
        {nebulas.map(nebula => {
          const nebulaColor = NEBULA_COLORS.find(c => c.id === nebula.colorId) || NEBULA_COLORS[0];
          const w = (nebula.width || 300) * s;
          const h = (nebula.height || 200) * s;
          return (
            <ellipse key={nebula.id} cx={toMiniX(nebula.x)} cy={toMiniY(nebula.y)} rx={w / 2} ry={h / 2} fill={nebulaColor.color} opacity={0.2} />
          );
        })}
        {/* Connections */}
        {connections.map(conn => {
          const from = nodes.find(n => n.id === conn.from);
          const to = nodes.find(n => n.id === conn.to);
          if (!from || !to) return null;
          return (
            <line key={conn.id}
              x1={toMiniX(from.x)} y1={toMiniY(from.y)}
              x2={toMiniX(to.x)} y2={toMiniY(to.y)}
              stroke="#f97316" strokeWidth={1} opacity={0.4} />
          );
        })}
        {/* Nodes */}
        {nodes.map(node => (
          <rect key={node.id}
            x={toMiniX(node.x) - 3} y={toMiniY(node.y) - 2}
            width={6} height={4} rx={1}
            fill={node.type === 'parameters' ? '#f97316' : '#6366f1'} />
        ))}
        {/* Viewport */}
        <rect x={vpX - vpWidth/2} y={vpY - vpHeight/2} width={vpWidth} height={vpHeight}
          fill="transparent" stroke="#fff" strokeWidth={1} opacity={0.5} rx={2} />
      </svg>
      {/* Click to navigate */}
      <div className="absolute inset-0 cursor-pointer"
        onClick={(e) => {
          const rect = e.currentTarget.getBoundingClientRect();
          const clickX = e.clientX - rect.left;
          const clickY = e.clientY - rect.top;
          const worldX = bounds.minX + clickX / s;
          const worldY = bounds.minY + clickY / s;
          onNavigate({ x: -worldX * zoom, y: -worldY * zoom });
        }} />
    </div>
  );
};

// ═══════════════════════════════════════════════════════════════════════════════════════
// ANIMATED CONNECTION
// ═══════════════════════════════════════════════════════════════════════════════════════

const AnimatedConnection = ({ from, to, color, order, zoom, onDelete, onEdit }) => {
  const [isHovered, setIsHovered] = useState(false);
  const [showControls, setShowControls] = useState(false);
  const midX = (from.x + to.x) / 2;
  const midY = (from.y + to.y) / 2 - Math.abs(to.x - from.x) * 0.15 - 20;
  const pathD = `M ${from.x} ${from.y} Q ${midX} ${midY} ${to.x} ${to.y}`;
  const gradientId = `grad-${from.id}-${to.id}-${order}`;
  
  // Keep controls visible for a bit after mouse leaves
  useEffect(() => {
    if (!isHovered && showControls) {
      const timer = setTimeout(() => setShowControls(false), 800);
      return () => clearTimeout(timer);
    }
  }, [isHovered, showControls]);
  
  const handleHover = (hovered) => {
    setIsHovered(hovered);
    if (hovered) setShowControls(true);
  };
  
  return (
    <g>
      <defs>
        <linearGradient id={gradientId} x1="0%" y1="0%" x2="100%" y2="0%">
          <stop offset="0%" stopColor={color} stopOpacity="0.1">
            <animate attributeName="stopOpacity" values="0.1;0.6;0.1" dur="2s" repeatCount="indefinite" />
          </stop>
          <stop offset="50%" stopColor={color} stopOpacity="0.9">
            <animate attributeName="offset" values="0;0.5;1" dur="2s" repeatCount="indefinite" />
          </stop>
          <stop offset="100%" stopColor={color} stopOpacity="0.1">
            <animate attributeName="stopOpacity" values="0.1;0.6;0.1" dur="2s" repeatCount="indefinite" />
          </stop>
        </linearGradient>
        <filter id={`glow-${gradientId}`} x="-50%" y="-50%" width="200%" height="200%">
          <feGaussianBlur stdDeviation="4" result="glow"/>
          <feMerge><feMergeNode in="glow"/><feMergeNode in="SourceGraphic"/></feMerge>
        </filter>
      </defs>
      
      {/* Glow */}
      <path d={pathD} fill="none" stroke={color} strokeWidth={6} opacity="0.15" filter={`url(#glow-${gradientId})`} />
      
      {/* Animated line */}
      <path d={pathD} fill="none" stroke={`url(#${gradientId})`} strokeWidth={2.5} strokeLinecap="round" />
      
      {/* Core */}
      <path d={pathD} fill="none" stroke={color} strokeWidth={1.2} opacity="0.8" />
      
      {/* Particles */}
      <circle r={4} fill={color} filter={`url(#glow-${gradientId})`}>
        <animateMotion dur="2.5s" repeatCount="indefinite" path={pathD} />
        <animate attributeName="opacity" values="0.4;1;0.4" dur="1s" repeatCount="indefinite" />
      </circle>
      <circle r={2.5} fill="white" opacity="0.8">
        <animateMotion dur="2.5s" repeatCount="indefinite" path={pathD} begin="-1.25s" />
      </circle>
      
      {/* Invisible hover area */}
      <path 
        d={pathD} 
        fill="none" 
        stroke="transparent" 
        strokeWidth={30} 
        style={{ cursor: 'pointer', pointerEvents: 'stroke' }}
        onMouseEnter={() => handleHover(true)}
        onMouseLeave={() => handleHover(false)}
      />
      
      {/* Order badge and controls - FIXED SIZE using inverse scale */}
      <g 
        transform={`translate(${midX}, ${midY + 5}) scale(${1/zoom})`}
        onMouseEnter={() => handleHover(true)}
        onMouseLeave={() => handleHover(false)}
        style={{ pointerEvents: 'all' }}
      >
        {/* Background for controls when hovered */}
        {showControls && (
          <rect 
            x={-44} 
            y={-14} 
            width={88} 
            height={28} 
            rx={14}
            fill="rgba(0,0,0,0.95)"
            stroke={color}
            strokeWidth={1.5}
          />
        )}
        
        {/* Delete button */}
        {showControls && (
          <g 
            transform="translate(-28, 0)" 
            style={{ cursor: 'pointer' }} 
            onClick={(e) => { e.stopPropagation(); onDelete(); }}
          >
            <circle r={10} fill="#dc2626" />
            <text textAnchor="middle" dominantBaseline="central" fill="white" fontSize={13} fontWeight="bold">×</text>
          </g>
        )}
        
        {/* Main badge with order number */}
        <g style={{ cursor: 'pointer' }}>
          <circle r={showControls ? 13 : 12} fill="#0a0a0f" stroke={color} strokeWidth={2} />
          <text textAnchor="middle" dominantBaseline="central" fill={color} fontSize={10} fontWeight="bold">{order}</text>
        </g>
        
        {/* Edit button */}
        {showControls && (
          <g 
            transform="translate(28, 0)" 
            style={{ cursor: 'pointer' }} 
            onClick={(e) => { e.stopPropagation(); onEdit(); }}
          >
            <circle r={10} fill="#1a1a2e" stroke={color} strokeWidth={1.5} />
            <text textAnchor="middle" dominantBaseline="central" fill={color} fontSize={9}>✎</text>
          </g>
        )}
      </g>
    </g>
  );
};

